import React, { useState } from "react";
import styles from "./Content.module.css";
import Button from "../../../common/Button/Button";

const Content = props => {
  const [lampColor, setLampColor] = useState("gold");
  return (
    <div className={styles.header__content}>
      <div className={styles.content_left}>
        <h1>премиальные сенсорные светильники для кухни</h1>
        <p>
          <span>8 лет</span> оснащаем Ваши кухни нашими светильниками по цене
          производителя.
        </p>
        <div>
          <span>Посмотрите</span> видеопрезентацию о наших светильниках!
        </div>
        <Button color={"yellow"} text={"онлайн конструктор"} />
      </div>
      <div className={styles.content_right}>
        <div className={styles.content_right__switch}>
          <div className={styles.switch_group}>
            <div className={styles.switch_gold}></div>
            <div className={styles.switch_white}></div>
            <div className={styles.switch_black}></div>
            <div className={styles.switch_gray}></div>
          </div>
        </div>
        <img src="/static/images/man.png" alt="man" />
        <div className={styles.content_right__lamp_variant}>
          <img src={`/static/images/lamp-${lampColor}.png`} alt="lamp" />
        </div>
      </div>
    </div>
  );
};

export default Content;
